import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import BookShow from './BookShow';
import BookSearch from './BookSearch';
import BookPagination from './BookPagination'; // Import BookPagination component
import './BookList.css';
import BookContext from '../context/book';

const apiURL = process.env.REACT_APP_API_URL;

const BookList = () => {
  const { books } = useContext(BookContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [booksPerPage] = useState(8); // Number of books per page
  const [filteredBooks, setFilteredBooks] = useState([]);

  // Fetch books from API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const resp = await axios.get(`${apiURL}/books`);
        setFilteredBooks(resp.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  // Function to handle search
  const searchBooks = async (term) => {
    setSearchTerm(term);
    try {
      const resp = await axios.get(`${apiURL}/books/search?term=${term}`);
      setFilteredBooks(resp.data);
    } catch (error) {
      console.error('Error searching books:', error);
    }
  };

  // Logic to paginate books
  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  const currentBooks = filteredBooks.slice(indexOfFirstBook, indexOfLastBook);

  // Function to handle page change
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className='book-list'>
      <BookSearch onSearch={searchBooks} />
      {currentBooks.length > 0 ? (
        currentBooks.map((book) => (
          <BookShow key={book.id} book={book} />
        ))
      ) : (
        <p>No books found</p>
      )}
      <BookPagination
        totalBooks={filteredBooks.length}
        booksPerPage={booksPerPage}
        currentPage={currentPage}
        onPageChange={handlePageChange}
      />
    </div>
  );
};

export default BookList;
