const BookShow = ({book}) => {

    const image = `https://picsum.photos/seed/${book.id}picsum/200/300`
  return (
    <div className="item"> 
    <div className="image">

        <img src={image} alt="" />
    </div>
        <h2>
          {book.title}
        </h2>
        <p>{book.desc}</p>
    </div>
  )
}

export default BookShow;
