import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BookList from './Components/BookList';
import BookCreate from './Components/BookCreate';
import BookPagination from './Components/BookPagination';
import useBookContext from './hook/useBookContext';
import './app.css';

const apiURL = process.env.REACT_APP_API_URL;

const App = () => {
  const [booksData, setBooksData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [bookPerPage] = useState(8);
  const { getAllBooks } = useBookContext();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const resp = await axios.get(`${apiURL}/books`);
        setBooksData(resp.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  const indexOfLastBook = currentPage * bookPerPage;
  const indexOfFirstBook = indexOfLastBook - bookPerPage;
  const currentBooks = booksData.slice(indexOfFirstBook, indexOfLastBook);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className='wrapper'>
      <div className="container">
        <h1>Reading Book</h1>
        {booksData.length === 0 ? (
          <p>Loading...</p>
        ) : (
          <>
            <div>
              <BookList booksData={currentBooks} />
            </div>
            <BookPagination
              totalBooks={booksData.length}
              booksPerPage={bookPerPage}
              currentPage={currentPage}
              onPageChange={handlePageChange}
            />
          </>
        )}
      </div>
      <BookCreate />
    </div>
  );
};

export default App;
