const apiURL = process.env.REACT_APP_API_URL;

export const createBook = async (term) => {
    const resp = await axios.post(`${apiURL}/books`, term);
    return resp.data;
};
export const fetchBook = async () => {
  
  const resp = await axios.get(`${apiURL}/books`);
  return resp.data;
};
