  }))
}
  useEffect(async () => {
    const tams = await fetchBook()
    setBooks(tams)
  }, [])
  return (
    <div className='wrapper'>
      <div className="container">

        <h1>Reading Book</h1>
        <div>
          <BookList books={books} onDelete={handleDelete} onEdit={handleUpdate}/>
        </div>
      </div>
      <BookCreate onCreate={handleCreate} />
    </div>
  )
}

export default App
