import React, { useState } from 'react';
import './BookCreate.css';

const BookCreate = ({ onCreate }) => {
  const [title, setTitle] = useState('');
  const [des, setDes] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'title') {
      setTitle(value);
    } else if (name === 'des') {
      setDes(value);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Kiểm tra xác thực dữ liệu đầu vào ở đây (ví dụ: kiểm tra title và des không rỗng)
      if (!title || !des) {
        throw new Error('Vui lòng nhập đủ thông tin.');
      }

      // Gọi hàm onCreate và chuyển dữ liệu
      await onCreate({ title, des });

      // Sau khi tạo mới thành công, reset trạng thái và hiển thị thông báo thành công (nếu cần)
      setTitle('');
      setDes('');
      setLoading(false);
    } catch (error) {
      // Xử lý lỗi và hiển thị thông báo lỗi cho người dùng
      setError(error.message);
      setLoading(false);
    }
  };

  return (
    <div className="form-create">
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title</label>
        <input
          type="text"
          id="title"
          name="title"
          placeholder="Nhập title"
          value={title}
          onChange={handleInputChange}
        />
        <label htmlFor="des">Description</label>
        <input
          type="text"
          id="des"
          name="des"
          placeholder="Nhập description"
          value={des}
          onChange={handleInputChange}
        />
        {error && <p className="error">{error}</p>}
        <button type="submit" disabled={loading}>
          {loading ? 'Creating...' : 'Create!'}
        </button>
      </form>
    </div>
  );
};

export default BookCreate;
