import React from 'react'
import { useState } from 'react'
import './BookCreate.css'
const BookCreate = ({ onCreate }) => {
  const [title, setTitle] = useState('title')
  const [des, setDes] = useState('description')
  const [error, setError] = useState()
  const handlChangeTitle = (e) => {
    setTitle(e.target.value);
  }
  const handlChangeDes = (e) => {
    setDes(e.target.value);
  }
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError()
    try{
      if (!title || !des){
        throw new Error('Vui lòng nhập đủ thông tin')
      }
      await onCreate({title, des})
      setTitle()
      setDes()
    } catch (error){
      setError(error.message)

    }
    // const reps = onCreate({
    //   title,
    //   des,
    // })
  }
  return (
    <div className='form-create'>
      <form onSubmit={handleSubmit}>
        <label htmlFor="Title">Title</label>
        <input onChange={handlChangeTitle} type="" id="title" name="title" placeholder='nhập title' />
        <label htmlFor="des">des</label>
        <input onChange={handlChangeDes} type="text" id="des" name="des" placeholder='nhập description'/>
        <input type="submit" value="Create!" />
        {error && <p className="error">{error}</p>}
      </form>
    </div>
  )
}

export default BookCreate
