import React from 'react'
import { useState } from 'react'
import './BookCreate.css'
const BookCreate = ({ onCreate }) => {
  const [title, setTitle] = useState('title')
  const [des, setDes] = useState('description')
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState()
  const handleInputChange = (e) => {
      const { name, value } = e.target;
      if (name === 'title') {
        setTitle(value)
      } else if (name === 'des') {
        setDes()
      }
  }
  const handlChangeTitle = (e) => {
    setTitle(e.target.value);
  }
  const handlChangeDes = (e) => {
    setDes(e.target.value);
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setError()
    const reps = onCreate({
      title,
      des,
    })
  }
  return (
    <div className='form-create'>
      <form onSubmit={handleSubmit}>
        <label htmlFor="Title">Title</label>
        <input onChange={handlChangeTitle} type="text" id="title" name="title" placeholder='nhập title' />
        <label htmlFor="des">des</label>
        <input onChange={handlChangeDes} type="text" id="des" name="des" placeholder='nhập description'/>
        <input type="submit" value="Create!" />
      </form>
    </div>
  )
}

export default BookCreate
