import BookCreate from './Components/BookCreate'
import BookList from './Components/BookList'
import './app.css'
import { useEffect } from 'react'
import useBookContext from './hook/useBookContex'
import { Provider } from './context/book'
const App = () => {
  const { getAllBooks } = useBookContext()
  useEffect(async () => {
    await getAllBooks();
  }, [])
  return (
    <Provider>

    <div className='wrapper'>
      <div className="container">
        <h1>Reading Book</h1>
        <div>
          <BookList />
        </div>
      </div>
      <BookCreate />
    </div>
    </Provider>
  )
}

export default App
