import React, { useContext, useState, useMemo } from 'react';
import BookShow from './BookShow';
import BookSearch from './BookSearch'; 
import BookPagination from './BookPagination'; // Bổ sung
import './BookList.css';
import BookContext from '../context/book';

const BookList = () => {
  const { books } = useContext(BookContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1); // Thêm trạng thái cho trang hiện tại
  const [booksPerPage] = useState(5); // Thêm trạng thái cho số lượng sách trên mỗi trang

  const filteredBooks = useMemo(() => {
    return books.filter((book) =>
      (book.title && book.title.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (book.des && book.des.toLowerCase().includes(searchTerm.toLowerCase())),
    );
  }, [books, searchTerm]);

  const searchBooks = (term) => {
    setSearchTerm(term);
    setCurrentPage(1); // Đặt lại trang về trang đầu tiên khi thực hiện tìm kiếm
  };

  // Tính toán chỉ số của sách đầu tiên và cuối cùng trên trang hiện tại
  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  // Lấy ra danh sách sách trên trang hiện tại
  const currentBooks = filteredBooks.slice(indexOfFirstBook, indexOfLastBook);

  // Hàm xử lý thay đổi trang
  const onPageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className='book-list'>
      <BookSearch onSearch={searchBooks} /> 
      {currentBooks.length > 0 ? (
        <>
          {currentBooks.map((book) => (
            <BookShow key={book.id} book={book} />
          ))}
          <BookPagination 
            totalBooks={filteredBooks.length}
            booksPerPage={booksPerPage}
            currentPage={currentPage}
            onPageChange={onPageChange}
          />
        </>
      ) : (
        <p>Không tìm thấy</p>
      )}
    </div>
  );
};

export default BookList;
