import BookCreate from './Components/BookCreate'
import BookList from './Components/BookList'
import axios from 'axios'
import { useState, useEffect } from 'react'
import './app.css'
import { fetchBook } from './api'

const App = () => {
  const [books, setBooks] = useState([])
  const fetchBook = async () => {
    const response = await axios.get("http://localhost:3001/books")
    setBooks(response.data)
  }
  const createBook = async (term) => {
    const resp = await axios.post("http://localhost:3001/books", term);
    if (resp){
      setBooks([...books, resp.data])
    }
}
const deleteBook = async (id) => {
    const dele = await axios.delete(`http://localhost:3001/books/${id}`);
    if (dele){
      fetchBook()
    }
  }
  const updateBook = async (term, id) => {
      const resp = await axios.put(`http://localhost:3001/books/${id}`,term);
      
    }
  useEffect(async () => {
    const tams = await fetchBook()
    setBooks(tams)
  }, [])
  return (
    <div className='wrapper'>
      <div className="container">

        <h1>Reading Book</h1>
        <div>
          <BookList books={books} onDeletesss={deleteBook} onEdit={updateBook}/>
        </div>
      </div>
      <BookCreate onCreate={createBook} />
    </div>
  )
}

export default App
