import { useState } from 'react'
import './BookCreate.css'
const BookCreate = ({ onCreate }) => {
  const [title, setTitle] = useState('title')
  const [des, setDes] = useState('description')
  const [suggestions, setSuggestions] = useState([]);

  const handlChangeTitle = (e) => {
    setTitle(e.target.value);
  }
  const handlChangeDes = (e) => {
    setDes(e.target.value);
  }
   const handleSubmit = async (e) => {
    e.preventDefault();
      await onCreate({
      title,
      des,
    })
  }
  const handleFocus = () => {
    // Fetch suggestions from somewhere (e.g., an API)
    // For now, let's assume suggestions are stored in an array
    const previousSuggestions = ['suggestion1', 'suggestion2', 'suggestion3']; // Example suggestions
    setSuggestions(previousSuggestions);
  };
  const handleSuggestionClick = (suggestion) => {
    setTitle(suggestion);
    setSuggestions([]); // Clear suggestions after selecting one
  };
  return (
    <div className='form-create'>
      <h3>Add a book</h3>
      <form onSubmit={handleSubmit}>
        <label htmlFor="Title">Title</label>
        <input onChange={handlChangeTitle} onFocus={handleFocus} type="text" id="title" name="title" placeholder='Nhập Title' />
        <label htmlFor="des">Des</label>
        <input onChange={handlChangeDes} type="text" id="des" name="des" placeholder='Nhập Des'/>
        <ul>
          {suggestions.map((suggestion, index) => (
            <li 
              key={index} 
              onClick={() => handleSuggestionClick(suggestion)}
            >
              {suggestion}
            </li>
          ))}
        </ul>
        <input type="submit" value="Create!" />
      </form>
    </div>
  )
}

export default BookCreate
