import BookCreate from './Components/BookCreate'
import BookList from './Components/BookList'
import axios from 'axios'
import { useState, useEffect } from 'react'
import './app.css'
import { fetchBook, createBook, deleteBook } from './api'

const App = () => {
  const [books, setBooks] = useState([])
  
//   const createBook = async (term) => {
//     const resp = await axios.post("http://localhost:3001/books", term);
//     if (resp){
//       setBooks([...books, resp.data])
//     }
// }
const handleCreate = async(term) => {
  const book = await createBook(term)
  if (book) setBooks([...books, book])     
}
const handleDelete = async (id) => {
  await deleteBook(id);
  fetchBook();
}
// const deleteBook = async (id) => {
//     const dele = await axios.delete(`http://localhost:3001/books/${id}`);
//     if (dele){
//       fetchBook()
//     }
//   }
  const updateBook = async (term, id) => {
      const resp = await axios.put(`http://localhost:3001/books/${id}`,term);
      
    }
  useEffect(async () => {
    const tams = await fetchBook()
    setBooks(tams)
  }, [])
  return (
    <div className='wrapper'>
      <div className="container">

        <h1>Reading Book</h1>
        <div>
          <BookList books={books} onDelete={handleDelete} onEdit={updateBook}/>
        </div>
      </div>
      <BookCreate onCreate={handleCreate} />
    </div>
  )
}

export default App
