import React from 'react'
import { useState } from 'react'
// import PropTypes from 'prop-types'
import './BookCreate.css'
const BookCreate = ({ onCreate,keywordList }) => {
  const [title, setTitle] = useState('title')
  const [des, setDes] = useState('description')
  const [error, setError] = useState()
  const [suggestedKeywords, setSuggestedKeywords] = useState([]);

  
  const handlChangeTitle = (e) => {
    const value = e.target.value;
    setTitle(value);
    const filteredKeywords = keywordList.filter(keyword =>
    keyword.toLowerCase().includes(value.toLowerCase())
    );
    setSuggestedKeywords(filteredKeywords);
  }
  const handlChangeDes = (e) => {
    setDes(e.target.value);
  }
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError()
    try{
      if (!title || !des){
        throw new Error('Vui lòng nhập đủ thông tin')
      }
      await onCreate({title, des})
      setTitle()
      setDes()
    } catch (error){
      setError(error.message)

    }
  }
  return (
    <div className='form-create'>
      <form onSubmit={handleSubmit}>
        <label className='title' htmlFor="Title">Title</label>
        <input onChange={handlChangeTitle} type="text" id="title" name="title" placeholder='Nhập Title' />
        <label htmlFor="des">Des</label>
        <input onChange={handlChangeDes} type="text" id="des" name="des" placeholder='Nhập Des'/>
        <input type="submit" value="Create!" />
        {error && <p className="error">{error}</p>}
        {suggestedKeywords.length > 0 && (
          <ul>
            {suggestedKeywords.map((keyword, index) => (
              <li key={index}>{keyword}</li>
            ))}
          </ul>
        )}
      </form>
    </div>
  )
}

export default BookCreate
