import BookEdit from "./BookEdit";
import { useState } from "react";
import './BookShow.css'
const BookShow = ({ book, onDelete, onEdit }) => {
  const image = `https://picsum.photos/seed/${book.id}picsum/200/300`
  const [isEdit, setIsEdit] = useState(false)
  const handleEdit = (id, term) => {
    onEdit(id, term)
    setIsEdit(false)
  }
  return (
    <div className="item">
      <div className="image">
      
        <img src={image} alt="" />
      </div >
      {!isEdit && (
        <>
          <h2>{book.title}</h2>
          <p>{book.des}</p>
        </>
      )}
      {isEdit && <BookEdit book={book} onEdit={handleEdit} />}
      {!isEdit && (

        <>
          <button className="deletebutton" onClick={() => onDelete(book.id)}>Delete</button>
          <button className="editbutton" onClick={() => setIsEdit(!isEdit)}>Edit</button>
        </>
      )}
    </div>
  )
}

export default BookShow;
