const App = () => {
  const [books, setBooks] = useState([])
  useEffect ( async () => {
    const tam = await fetchBook();
    console.log(tam)
  }, [])
  return (
  <div className='wrapper'>
    <h1>Reading Book</h1>
    <div>
      <BookList books={books} />
    </div>
    <BookCreate onCreate={createBook}/>
  </div>
  )
}

export default App
