import { useState } from 'react'
import './BookCreate.css'
const BookCreate = ({ onCreate }) => {
  const [title, setTitle] = useState('title')
  const [des, setDes] = useState('description')
  const [titleSuggestions, setTitleSuggestions] = useState([]);
  const handleChangeTitle = (e) => {
    const inputValue = e.target.value;
    setTitle(inputValue);

    // Lọc danh sách gợi ý từ các giá trị đã nhập trước đó
    const filteredSuggestions = titleSuggestions.filter(suggestion =>
      suggestion.toLowerCase().includes(inputValue.toLowerCase())
    );

    // Nếu giá trị nhập không phải là một phần của danh sách gợi ý đã nhập trước đó, thêm vào danh sách gợi ý
    if (!filteredSuggestions.includes(inputValue)) {
      setTitleSuggestions([...filteredSuggestions, inputValue]);
    }
  }
  const handlChangeDes = (e) => {
    setDes(e.target.value);
  }
   const handleSubmit = async (e) => {
    e.preventDefault();
      await onCreate({
      title,
      des,
    })
  }
  return (
    <div className='form-create'>
      <h3>Add a book</h3>
      <form onSubmit={handleSubmit}>
        <label htmlFor="Title">Title</label>
        <input onChange={handlChangeTitle} type="text" id="title" name="title" placeholder='Nhập Title' />
        <ul>
          {titleSuggestions.map((suggestion, index) => (
            <li key={index}>{suggestion}</li>
          ))}
        </ul>
        <label htmlFor="des">Des</label>
        <input onChange={handlChangeDes} type="text" id="des" name="des" placeholder='Nhập Des'/>
        <input type="submit" value="Create!" />
      </form>
    </div>
  )
}

export default BookCreate
