  const [books, setBooks] = useState([]);

    const fetchBooks = async () => {
      const reponse = await axios.get('http://localhost:3000/')

      setBooks(Response)
    };
    useEffect(async () => {
      setLoading(true);
      const photos = await getAllPhotos();
      setBooks(photos);
      setLoading(false);
    }, []);
  return (
    <>
     <BookCreate functionSearchParent={fetchBooks}/>
     {!loading && <BookList books={books}/>}
     {loading && (<>Dang load hinh anh</>)}
     </>
  );
};

export default App;
